import React, { Component } from "react";
import ReactDOM from "react-dom";

class Products extends Component {
  constructor() {
    super();
    this.state = {
      products: []
    };
  }
  componentDidMount() {
    fetch("/api/products")
      .then(res => res.json())
      .then(
        data => {
          console.log(data);
          this.setState({
            products: data
          });
        },
        err => console.log(err)
      );
  }
  render() {
    return (
      <div>
        <title id="product-title">Explore Our Parts</title>
        {this.state.products.map(products => {
          return (
            <div className="productCard">
              <title className="productCard-title">
                {products.product_name}
              </title>
              <img
                src={products.product_image}
                className="productCard-img-top"
              />
              <div className="productCard-body">
                <section className="category">
                  {products.product_category}
                </section>
                <p className="productCard-text">
                  {products.product_description}
                </p>
                <section className="price">{products.price}</section>
                <a href="#" className="pbtn pbtn-primary">
                  Explore
                </a>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}
export default Products;
