import React, { Component } from "react";
import ReactDOM from "react-dom";

class Products extends Component {
  componentDidMount() {
    fetch("/api/products")
      .then(res => res.json())
      .then(
        data => {
          console.log(data);
          this.setState({
            products: data
          });
        },
        err => console.log(err)
      );
  }
  render() {
    return (
      <div>
        <div className="card" style="width: 18rem;">
          <title className="card-title">
            {this.state.products.product_name}
          </title>
          <img
            src={this.state.products.product_image}
            className="card-img-top"
          />
          <div className="card-body">
            <section className="category">
              {this.state.product.product_category}
            </section>
            <p className="card-text">
              {this.state.products.product_description}
            </p>
            <section className="price">{this.state.products.price}</section>
            <a href="#" className="btn btn-primary">
              Explore
            </a>
          </div>
        </div>
      </div>
    );
  }
}
export default Products;
