import React, { Component } from "react";
import ReactDOM from "react-dom";

class Products extends Component {
  constructor() {
    super();
    this.state = {
      products: []
    };
    //filter will give you back all products in the exterior category
    this.exteriorProducts = e => {
      fetch("/api/productfilter/Exterior")
        .then(res => res.json())
        .then(
          data => {
            console.log(data);
            this.setState({
              products: data
            });
          },
          err => console.log(err)
        );
    };

    //filter will give you back all products in the Performance category
    this.performanceProducts = e => {
      fetch("/api/productfilter/Performance")
        .then(res => res.json())
        .then(
          data => {
            console.log(data);
            this.setState({
              products: data
            });
          },
          err => console.log(err)
        );
    };
    // filter will give you back all products under $300.
    this.productUnder300 = e => {
      fetch("/api/pricefilter/300")
        .then(res => res.json())
        .then(
          data => {
            console.log(data);
            this.setState({
              products: data
            });
          },
          err => console.log(err)
        );
    };
    // will give you back all products under $600.
    this.productUnder600 = e => {
      fetch("/api/pricefilter/600")
        .then(res => res.json())
        .then(
          data => {
            console.log(data);
            this.setState({
              products: data
            });
          },
          err => console.log(err)
        );
    };
    this.productUnder1000 = e => {
      fetch("/api/pricefilter/1000")
        .then(res => res.json())
        .then(
          data => {
            console.log(data);
            this.setState({
              products: data
            });
          },
          err => console.log(err)
        );
    };
  }
  componentDidMount() {
    fetch("/api/products")
      .then(res => res.json())
      .then(
        data => {
          console.log(data);
          this.setState({
            products: data
          });
        },
        err => console.log(err)
      );
  }

  render() {
    return (
      <div className="wrapper">
        {this.state.products.map(products => {
          return (
            <div className="productCard">
              <title className="productCard-title">
                {products.product_name}
              </title>
              <img
                src={products.product_image}
                className="productCard-img-top"
              />
              <div className="productCard-body">
                <section className="category">
                  {products.product_category}
                </section>
                <p className="productCard-text">
                  {products.product_description}
                </p>
                <section className="price">Price: {products.price}</section>
                <a href="" className="pbtn pbtn-primary">
                  Buy
                </a>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}
export default Products;
