import React, { Component } from "react";
import ReactDOM from "react-dom";

class Products extends Component {
  constructor() {
    super();
    this.state = {
      products: []
    };
  }
  componentDidMount() {
    fetch("/api/products")
      .then(res => res.json())
      .then(
        data => {
          console.log(data);
          this.setState({
            products: data
          });
        },
        err => console.log(err)
      );
  }
  render() {
    return (
      <div>
        {this.state.products.map(products => {
          return (
            <div className="card" style="width: 18rem;">
              <title className="card-title">{products.product_name}</title>
              <img src={products.product_image} className="card-img-top" />
              <div className="card-body">
                <section className="category">
                  {products.product_category}
                </section>
                <p className="card-text">{products.product_description}</p>
                <section className="price">{products.price}</section>
                <a href="#" className="btn btn-primary">
                  Explore
                </a>
              </div>
            </div>
          );
        })}
      </div>
    );
  }
}
export default Products;
